        $classNames = [];
        $classNames[] = 'MU\BloggingModule\Entity\PostEntity';
        $classNames[] = 'MU\BloggingModule\Entity\PostTranslationEntity';
        $classNames[] = 'MU\BloggingModule\Entity\PostCategoryEntity';
    
        return $classNames;
    }
    
    /**
